CREATE TABLE Accounts(
  	AccNo BIGINT,
    CID BIGINT NOT NULL, 
    BranchNo MEDIUMINT NOT NULL,
    AccType VARCHAR(10) NOT NULL, 
    Balance BIGINT, 
    DOC DATE NOT NULL, 
    InterestAmount INT, 
    NomineeId INT,
    PRIMARY KEY(AccNo),
    FOREIGN KEY (CID) REFERENCES Customer(CID),
    FOREIGN KEY (BranchNo) REFERENCES Branch(BranchNo)
    );


